<template>
  <div class="login-container">
    <el-form
      ref="loginForm"
      :model="loginForm"
      :rules="loginRules"
      class="login-form"
      auto-complete="on"
      label-position="left"
    >
      <div class="title-container">
        <div class="tx-c">
          <div class="white">
            <a href="/" class="flexbox-hvc">
              <img
                alt="logo"
                width="44"
                class="antd-pro-layouts-user-layout-logo"
                src="data:image/svg+xml;base64,"
              >
              <span class="fs20 tx-pc pl20">Ant Design</span>
            </a>
          </div>
          <div class="pt15 pb30 tx-sc">Ant Design 是西湖区最具影响力的 Web 设计规范</div>
        </div>
      </div>

      <el-form-item prop="username">
        <span class="svg-container">
          <svg-icon icon-class="user"/>
        </span>
        <el-input
          v-model="loginForm.username"
          :placeholder="'请输入用户名'"
          name="username"
          type="text"
          auto-complete="on"
        />
      </el-form-item>

      <el-form-item prop="password">
        <span class="svg-container">
          <svg-icon icon-class="password"/>
        </span>
        <el-input
          v-model="loginForm.password"
          :placeholder="'请输入密码'"
          name="password"
          type="password"
          auto-complete="on"
        />
      </el-form-item>
      <el-form-item prop="password">
        <span class="svg-container">
          <svg-icon icon-class="password"/>
        </span>
        <el-input
          v-model="loginForm.confirmPassword"
          :placeholder="'请确认密码'"
          type="password"
          auto-complete="on"
        />
      </el-form-item>

      <div class="mb15">
        <el-button
          :loading="loading"
          type="primary"
          size="large"
          style="width:100%; "
          @click="register"
        >注册</el-button>
      </div>
      <div class="item-form mb10" style="margin-top:-15px">
        <div class="flexbox">
          <div class="flex">
            <div class="tx-c white">
              <router-link to="/login">
                <el-button type="text" size="tiny">已有账号</el-button>
              </router-link>
            </div>
          </div>
        </div>
      </div>
    </el-form>
  </div>
</template>

<script>
// import { mapActions } from "vuex";
import {
  validateConfirmPassword,
  validatePassword,
  validateUsername
} from "./utils";
import { userApi } from "../../api";
export default {
  name: "Register",
  data() {
    return {
      loginForm: {
        username: "zenberge",
        password: "123456",
        confirmPassword: "123456"
      },
      loginRules: {
        username: [
          { required: true, trigger: "blur", validator: validateUsername }
        ],
        password: [
          { required: true, trigger: "blur", validator: validatePassword }
        ],
        confirmPassword: [
          {
            required: true,
            trigger: "blur",
            validator: validateConfirmPassword
          }
        ]
      },
      loading: false,
      redirect: undefined
    };
  },
  created() {
    // window.addEventListener('hashchange', this.afterQRScan)
  },
  destroyed() {
    // window.removeEventListener('hashchange', this.afterQRScan)
  },
  methods: {
    register() {
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true;
          this.$http
            .post(userApi.register, {
              name: this.loginForm.username,
              password: this.loginForm.password
            })
            .then(() => {
              this.loading = false;
              this.$router.push({ path: "/dashboard" });
            })
            .catch(() => {
              this.loading = false;
            });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  },

  afterQRScan() {
    // const hash = window.location.hash.slice(1)
    // const hashObj = getQueryObject(hash)
    // const originUrl = window.location.origin
    // history.replaceState({}, '', originUrl)
