    // if (!codeName) {
    //   alert('第三方登录失败')
    // } else {
    //   this.$store.dispatch('LoginByThirdparty', codeName).then(() => {
    //     this.$router.push({ path: '/' })
    //   })
    // }
  }
};
</script>

